#!/usr/bin/env bash

plink --bfile plink_6 --hardy

# Selecting SNPs with HWE p-value below 0.00001, required for one of the two
# plot generated by the next Rscript, allows to zoom in on strongly deviating
# SNPs. 

awk '{ if ($3=="TEST" || $3=="UNAFF" && $9 <0.0000001) print $0 }' \
	plink.hwe > plinkzoomhwe.hwe

plink --bfile plink_6 --hwe 1e-7 --make-bed --out plink_7

